import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

import { LanguageService } from '@igo2/core';

@Component({
  selector: 'app-request',
  templateUrl: './request.component.html',
  styleUrls: ['./request.component.scss']
})
export class AppRequestComponent {
  constructor(
    private http: HttpClient,
    public languageService: LanguageService
  ) {}

  callHttp() {
    const url = 'https://geoegl.msp.gouv.qc.ca/apis/icherche/info';
    this.http.get(url).subscribe(rep => {
      console.log(rep);
    });
  }

  callHttpError() {
    const url = '404';
    this.http.get(url).subscribe(rep => {
      console.log(rep);
    });
  }
}
<mat-card>
  <mat-card-subtitle>Core</mat-card-subtitle>
  <mat-card-title>Request</mat-card-title>
  <mat-card-content>
    <p>Register http calls in console</p>

    <li>Import <code>IgoLoggingModule</code> only if you want register http calls in console</li>
    <li>Import <code>IgoErrorModule</code> only if you want register errors from http call in console</li>
    <li>Import modules only once</li>

    <br>
    See the <a href="https://github.com/infra-geo-ouverte/igo2-lib/tree/master/demo/src/app/core/request">code of this example</a>
    <hr>
  </mat-card-content>

  <mat-card-actions>
    <button mat-raised-button (click)="callHttp()">Log</button>
    <button mat-raised-button (click)="callHttpError()">Error</button>
  </mat-card-actions>

</mat-card>
