import { Component } from '@angular/core';
import { ConfigService, LanguageOptions } from '@igo2/core';

@Component({
  selector: 'app-config',
  templateUrl: './config.component.html',
  styleUrls: ['./config.component.scss']
})
export class AppConfigComponent {
  public configLanguage: LanguageOptions;

  constructor(private configService: ConfigService) {
    this.configLanguage = this.configService.getConfig('language');
  }
}
<mat-card>
  <mat-card-subtitle>Core</mat-card-subtitle>
  <mat-card-title>config</mat-card-title>
  <mat-card-content>
    <p>Load config in your angular application</p>

    <li>Import <code>IgoConfigModule</code> only once</li>

    <br>
    See the <a href="https://github.com/infra-geo-ouverte/igo2-lib/tree/master/demo/src/app/core/config"> code of this example</a> <br>
    See the <a href="https://github.com/infra-geo-ouverte/igo2-lib/tree/master/demo/src/environments"> environment config</a>
    <hr>
  </mat-card-content>

  <p>Config language: {{configLanguage | json }}<p>

</mat-card>
