import { Component } from '@angular/core';

import { LanguageService } from '@igo2/core';
import { IgoMap, OverlayService, MapService } from '@igo2/geo';

@Component({
  selector: 'app-context',
  templateUrl: './context.component.html',
  styleUrls: ['./context.component.scss']
})
export class AppContextComponent {
  public map = new IgoMap({
    controls: {
      attribution: {
        collapsed: true
      }
    }
  });

  public view = {
    center: [-73, 47.2],
    zoom: 6
  };

  constructor(
    private languageService: LanguageService,
    private mapService: MapService,
    private overlayService: OverlayService
  ) {
    this.mapService.setMap(this.map);
  }
}
<mat-card>
  <mat-card-subtitle>Geo</mat-card-subtitle>
  <mat-card-title>Context</mat-card-title>
  <mat-card-content>
    <p>* Dependencies: LanguageService</p>

    <br>
    See the <a href="https://github.com/infra-geo-ouverte/igo2-lib/tree/master/demo/src/app/context/context">code of this example</a><br>
    See <a href="https://github.com/infra-geo-ouverte/igo2-lib/tree/master/demo/src/contexts"> contexts</a>
    <hr>
  </mat-card-content>

  <igo-map-browser
    igoMapContext
    igoLayerContext
    [map]="map">
    <igo-zoom-button [map]="map" color="primary"></igo-zoom-button>
    <igo-geolocate-button [map]="map" color="primary"></igo-geolocate-button>
    <igo-home-extent-button [map]="map" color="primary"></igo-home-extent-button>
    <igo-rotation-button [map]="map" [showIfNoRotation]="true" color="primary"></igo-rotation-button>
  </igo-map-browser>

  <igo-panel title="Contexts list">
    <igo-context-list igoContextListBinding [map]="map"></igo-context-list>
  </igo-panel>

  <igo-panel title="Layers">
    <igo-layer-list igoLayerListBinding [map]="map">
      <ng-template #igoLayerItemToolbar let-layer="layer">
        <igo-metadata-button [layer]="layer"></igo-metadata-button>
      </ng-template>
    </igo-layer-list>
  </igo-panel>

</mat-card>
