import { Component } from '@angular/core';

import { MediaService } from '@igo2/core';

@Component({
  selector: 'app-media',
  templateUrl: './media.component.html',
  styleUrls: ['./media.component.scss']
})
export class AppMediaComponent {
  constructor(private mediaService: MediaService) {}

  get media() {
    return this.mediaService.getMedia();
  }

  get orientation() {
    return this.mediaService.getOrientation();
  }

  get isTouchScreen() {
    return this.mediaService.isTouchScreen();
  }

}
<mat-card>
  <mat-card-subtitle>Core</mat-card-subtitle>
  <mat-card-title>Media</mat-card-title>
  <mat-card-content>
    <p>Detect type of media (mobile, tablet, desktop)</p>

    <br>
    See the <a href="https://github.com/infra-geo-ouverte/igo2-lib/tree/master/demo/src/app/core/media">code of this example</a>
    <hr>
  </mat-card-content>

  <p>Current media: {{media}}</p>
  <p>Current orientation: {{orientation}}</p>
  <p>Device mode:  {{isTouchScreen ? 'Touch device' : 'Not a touch device '}}</p>
</mat-card>
