import { Component } from '@angular/core';

import { LanguageService } from '@igo2/core';
import {
  IgoMap,
  LayerService,
  MapService,
  ProjectionService,
  StopsStore,
  StopsFeatureStore,
  RoutesFeatureStore,
  StepFeatureStore
} from '@igo2/geo';
import { Subject } from 'rxjs';

@Component({
  selector: 'app-directions',
  templateUrl: './directions.component.html',
  styleUrls: ['./directions.component.scss']
})
export class AppDirectionsComponent {
  public map = new IgoMap({
    controls: {
      attribution: {
        collapsed: true
      }
    }
  });

  public view = {
    center: [-73, 47.2],
    zoom: 9,
    geolocate: false
  };

  public stopsStore: StopsStore = new StopsStore([]);
  public stopsFeatureStore: StopsFeatureStore = new StopsFeatureStore([], { map: this.map });
  public stepFeatureStore: StepFeatureStore = new StepFeatureStore([], { map: this.map });
  public routesFeatureStore: RoutesFeatureStore = new RoutesFeatureStore([], { map: this.map });
  public zoomToActiveRoute$: Subject<void> = new Subject();

  constructor(
    private projectionService: ProjectionService,
    private languageService: LanguageService,
    private layerService: LayerService,
    private mapService: MapService
  ) {
    this.mapService.setMap(this.map);
    this.layerService
      .createAsyncLayer({
        title: 'OSM',
        sourceOptions: {
          type: 'osm'
        }
      })
      .subscribe(l => this.map.addLayer(l));
  }
}
<mat-card>
  <mat-card-subtitle>Geo</mat-card-subtitle>
  <mat-card-title>Directions</mat-card-title>
  <mat-card-content>
    <li>Dependencies: LanguageService</li>

    <br>
    See the <a href="https://github.com/infra-geo-ouverte/igo2-lib/tree/master/demo/src/app/geo/directions">code of this example</a>
    <hr>
  </mat-card-content>

  <igo-map-browser [map]="map" [view]="view">
    <igo-zoom-button [map]="map" color="primary"></igo-zoom-button>
    <igo-geolocate-button [map]="map" color="primary"></igo-geolocate-button>
    <igo-home-extent-button [map]="map" color="primary"></igo-home-extent-button>
    <igo-rotation-button [map]="map" [showIfNoRotation]="false" color="primary"></igo-rotation-button>
  </igo-map-browser>

  <igo-directions
    [stopsStore]="stopsStore"
    [stopsFeatureStore]="stopsFeatureStore"
    [stepFeatureStore]="stepFeatureStore"
    [routesFeatureStore]="routesFeatureStore"
    [zoomToActiveRoute$]="zoomToActiveRoute$">  
  </igo-directions>



</mat-card>
