import { Component } from '@angular/core';

import { LanguageService } from '@igo2/core';
import {
  IgoMap,
  DataSourceService,
  LayerService,
  WMSDataSourceOptions,
  LayerOptions,
  WFSDataSourceOptions,
  OgcFilterableDataSourceOptions,
  MetadataLayerOptions
} from '@igo2/geo';

@Component({
  selector: 'app-legend',
  templateUrl: './legend.component.html',
  styleUrls: ['./legend.component.scss']
})
export class AppLegendComponent {
  public map = new IgoMap({
    controls: {
      attribution: {
        collapsed: true
      }
    }
  });

  public view = {
    center: [-73, 47.2],
    zoom: 7
  };

  constructor(
    private languageService: LanguageService,
    private dataSourceService: DataSourceService,
    private layerService: LayerService
  ) {
    this.dataSourceService
      .createAsyncDataSource({
        type: 'osm'
      })
      .subscribe(dataSource => {
        this.map.addLayer(
          this.layerService.createLayer({
            title: 'OSM',
            visible: true,
            baseLayer: true,
            source: dataSource
          })
        );
      });

    interface WFSoptions
      extends WFSDataSourceOptions,
        OgcFilterableDataSourceOptions {}

    const wfsDatasource: WFSoptions = {
      type: 'wfs',
      url: 'https://geoegl.msp.gouv.qc.ca/apis/ws/igo_gouvouvert.fcgi',
      params: {
        featureTypes: 'vg_observation_v_autre_wmst',
        fieldNameGeometry: 'geometry',
        maxFeatures: 10000,
        version: '2.0.0',
        outputFormat: undefined,
        outputFormatDownload: 'shp'
      },
      ogcFilters: {
        enabled: true,
        editable: true,
        filters: {
          operator: 'PropertyIsEqualTo',
          propertyName: 'code_municipalite',
          expression: '10043'
        }
      }
    };

    this.layerService
    .createAsyncLayer({
      title: 'legend with display:false',
      visible: true,
      legendOptions: {
        display: false,
        html: 'test html'
      },
      sourceOptions: {
        type: 'wms',
        url: 'https://geoegl.msp.gouv.qc.ca/apis/ws/mffpecofor.fcgi',
        optionsFromCapabilities: true,
        params: {
          LAYERS: 'sh_dis_eco',
          VERSION: '1.3.0'
        }
      }
    })
    .subscribe(l => this.map.addLayer(l));

    this.layerService
    .createAsyncLayer({
      title: 'legend in html',
      visible: true,
      legendOptions: {
        display: true,
        html: '<h2 style="background-color: blue;">HTML l√©gende</h2>'
      },
      sourceOptions: {
        type: 'wms',
        url: 'https://geoegl.msp.gouv.qc.ca/apis/ws/mffpecofor.fcgi',
        optionsFromCapabilities: false,
        params: {
          LAYERS: 'sh_sreg_eco',
          VERSION: '1.3.0'
        }
      }
    })
    .subscribe(l => this.map.addLayer(l));

    this.layerService
    .createAsyncLayer({
      title: 'legend with url param',
      visible: true,
      legendOptions: {
        url: `data:image/jpeg;base64,`

      },
      sourceOptions: {
        type: 'wms',
        url: 'https://geoegl.msp.gouv.qc.ca/apis/ws/mffpecofor.fcgi',
        optionsFromCapabilities: true,
        params: {
          LAYERS: 'sh_reg_eco',
          VERSION: '1.3.0'
        }
      }
    })
    .subscribe(l => this.map.addLayer(l));

    this.dataSourceService
      .createAsyncDataSource(wfsDatasource)
      .subscribe(dataSource => {
        const layer: LayerOptions = {
          title: 'WFS ',
          visible: true,
          source: dataSource
        };
        this.map.addLayer(this.layerService.createLayer(layer));
      });

    this.layerService
      .createAsyncLayer({
        sourceOptions: {
          type: 'wms',
          url: 'https://geoegl.msp.gouv.qc.ca/apis/ws/igo_gouvouvert.fcgi',
          optionsFromCapabilities: true,
          params: {
            LAYERS: 'MELS_CS_ANGLO_S',
            VERSION: '1.3.0'
          }
        }
      })
      .subscribe(l => this.map.addLayer(l));

    this.layerService
      .createAsyncLayer({
        title: 'R√©seau routier',
        visible: false,
        sourceOptions: {
          type: 'wms',
          url: 'https://ws.mapserver.transports.gouv.qc.ca/swtq',
          params: {
            LAYERS: 'bgr_v_sous_route_res_sup_act',
            VERSION: '1.3.0'
          }
        }
      })
      .subscribe(l => this.map.addLayer(l));

    this.layerService
      .createAsyncLayer({
        title: 'lieu habit√©',
        visible: false,
        sourceOptions: {
          type: 'wms',
          url: 'https://ws.mapserver.transports.gouv.qc.ca/swtq',
          optionsFromCapabilities: true,
          params: {
            LAYERS: 'lieuhabite',
            VERSION: '1.3.0'
          }
        }
      })
      .subscribe(l => this.map.addLayer(l));

    this.layerService
      .createAsyncLayer({
        title: '2 styles nurc:Arc_Sample_Parent',
        visible: true,
        legendOptions: {
          // collapsed: false,
          display: true,
          // url: 'https://v.seloger.com/s/width/1144/visuels/0/m/l/4/0ml42xbt1n3itaboek3qec5dtskdgw6nlscu7j69k.jpg',
          stylesAvailable: [
            { name: 'rain', title: 'Pluie' },
            { name: 'raster', title: 'D√©faut' }
          ] //
        },
        sourceOptions: {
          type: 'wms',
          url: 'https://demo.geo-solutions.it/geoserver/ows',
          optionsFromCapabilities: true,
          params: {
            LAYERS: 'nurc:Arc_Sample', // , test:Linea_costa
            VERSION: '1.3.0'
          }
        }
      })
      .subscribe(l => this.map.addLayer(l));

    this.layerService
      .createAsyncLayer({
        title: 'Avertissements routier',
        visible: false,
        sourceOptions: {
          type: 'wms',
          url: 'https://ws.mapserver.transports.gouv.qc.ca/swtq',
          params: {
            LAYERS: 'evenements',
            VERSION: '1.3.0'
          }
        }
      })
      .subscribe(l => this.map.addLayer(l));

    const datasource: WMSDataSourceOptions = {
      type: 'wms',
      url: 'https://geoegl.msp.gouv.qc.ca/apis/ws/igo_gouvouvert.fcgi',
      refreshIntervalSec: 15,
      params: {
        LAYERS: 'vg_observation_v_inondation_embacle_wmst',
        VERSION: '1.3.0'
      }
    };

    interface LayerOptionsWithMetadata
      extends LayerOptions,
        MetadataLayerOptions {}

    this.dataSourceService
      .createAsyncDataSource(datasource)
      .subscribe(dataSource => {
        const layer: LayerOptionsWithMetadata = {
          title: 'Emb√¢cle',
          visible: true,
          source: dataSource,
          metadata: {
            url:
              'https://www.donneesquebec.ca/recherche/fr/dataset/historique-publique-d-embacles-repertories-au-msp',
            extern: true
          }
        };
        this.map.addLayer(this.layerService.createLayer(layer));
      });
  }
}
<mat-card>
  <mat-card-subtitle>Geo</mat-card-subtitle>
  <mat-card-title>Simple Map with a legend list</mat-card-title>
  <mat-card-content>
    <li>Dependencies: LanguageService</li>

    <br>
    See the <a href="https://github.com/infra-geo-ouverte/igo2-lib/tree/master/demo/src/app/geo/layer">code of this
      example</a>
    <hr>
  </mat-card-content>

  <igo-map-browser [map]="map" [view]="view">
    <igo-zoom-button [map]="map" color="primary"></igo-zoom-button>
  </igo-map-browser>

  <igo-panel title="Legends">
    <igo-layer-legend-list [layers]="map.layers"
    [excludeBaseLayers]="true"
    [allowShowAllLegends]="true"
    [updateLegendOnResolutionChange]="false"
    [showAllLegendsValue]="false">
  </igo-layer-legend-list>

  </igo-panel>

</mat-card>
