import { Component } from '@angular/core';

import { ActivityService } from '@igo2/core';

@Component({
  selector: 'app-activity',
  templateUrl: './activity.component.html',
  styleUrls: ['./activity.component.scss']
})
export class AppActivityComponent {
  private idsActivity: string[] = [];

  constructor(private activityService: ActivityService) {}

  register() {
    this.idsActivity.push(this.activityService.register());
  }

  unregister() {
    this.activityService.unregister(this.idsActivity.pop());
  }

  get counter() {
    return this.activityService.counter$.value;
  }
}
<mat-card>
  <mat-card-subtitle>Core</mat-card-subtitle>
  <mat-card-title>Activity</mat-card-title>
  <mat-card-content>
    <p>Register activities in service. Use to display a loading icon</p>

    <li>Import <code>IgoActivityModule</code> only if you want register http calls</li>
    <li>Import <code>IgoActivityModule</code> only once</li>

    <br>
    See the <a href="https://github.com/infra-geo-ouverte/igo2-lib/tree/master/demo/src/app/core/activity"> code of this example</a>
    <hr>
  </mat-card-content>

  <mat-card-actions>
    <button mat-raised-button (click)="register()">Register</button>
    <button mat-raised-button (click)="unregister()">Unregister</button>
  </mat-card-actions>
  <p>Counter: {{counter}}</p>

  <igo-spinner igoSpinnerActivity></igo-spinner>

</mat-card>
